package conditional;

public class Nestedif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		

			int x = 80;
			int y = 50;
			
			if (x==80){
				System.out.println("value of x is "+x);
				
				if (y==30){
					System.out.println("value of y is "+y);
				}
			}
	}
}
			
		
	
		
		
			


