package conditional;

public class Unary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1;
		int b=a++;
		System.out.println("a value is:" +a);
		int i=10;
		int j=++i;
		System.out.println("i value is:" +i);
		int x=5;
		int y=--x;
		System.out.println("x value is:" +x);
		

		int p=20;
		int q=p--;
		System.out.println("p value is:" +p);
		

		

	}

}
